mapper.select:
select b.id, b.title, b.content, b.hit, b.like,  b.createdAt, m.id as member_id,  m.nickname as member_nickname
from boards b join members m
on b.member_id = m.id
where b.category = #{category}

order by b.id desc
limit #{start}, #{listsize}

mapper.update:
update boards
set hit = hit + 1
where id = #{id}

update boards
set isDeleted = 1
where id = #{id};
